  String _autoWeekForStage(String stage) {
    if (_type == 'Chair') {
      if (stage == 'Démarrage') return '1';
      if (stage == 'Croissance') return '4';
      if (stage == 'Finition') return '6';
    } else if (_type == 'Pondeuse') {
      if (stage == 'Démarrage') return '1';
      if (stage == 'Croissance') return '9';
      if (stage == 'Ponte') return '19';
    } else if (_type == 'Mpiady') {
      if (stage == 'Démarrage') return '1';
      if (stage == 'Croissance') return '9';
      if (stage == 'Ady') return '24';
    } else if (_type == 'Kisoa') {
      if (stage == 'Zaza (Sevrage)') return '6';
      if (stage == 'Mitombo (Croissance)') return '12';
      if (stage == 'Lehibe (Finition)') return '20';
    } else {
      if (stage == 'Démarrage') return '1';
      if (stage == 'Croissance') return '9';
      if (stage == 'Adulte' || stage == 'Ponte') return '20';
    }
    return '1';
  }

  String _buildTieredFormula(List<Map<String, dynamic>> tiers) {
    return tiers
        .map((tier) {
          final lines = (tier['lines'] as List<String>).map((line) => '   • $line').join('\n');
          return '🔹 ${tier['label']}:\n$lines';
        })
        .join('\n\n');
  }

