  String _getBreedEmoji(String type) {
    switch (type) {
      case 'Pondeuse': return '🥚';
      case 'Chair': return '🍗';
      case 'Ornement': return '🦚';
      case 'Mpiady': return '🐓';
      case 'Vorontsiloza': return '🦃';
      case 'Akanga': return '🐦';
      case 'Papelika': return '🐣';
      default: return '🐔';
    }
  }

