            'Fandaniana hafa': '${NumberFormat("#,##0").format(otherCost)} Ar',
            '---': '---',
            'VOLA LANY REHETRA': '${NumberFormat("#,##0").format(totalCost)} Ar',
            'Vokatra andrasana': '${totalYieldKg.toStringAsFixed(1)} kg',
            'Vidiny/kg': '${NumberFormat("#,##0").format(costPerKg)} Ar',
