  void _showAllCodesDialog(List<Map<String, dynamic>> codes) {
    showDialog(
      context: context,
      builder: (ctx) => AlertDialog(
        shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(20)),
        title: const Text('Tous les codes'),
        content: SizedBox(
          width: double.maxFinite,
          height: 400,
          child: codes.isEmpty
              ? const Center(child: Text('Aucun code généré'))
              : ListView.builder(
                  itemCount: codes.length,
                  itemBuilder: (context, index) {
                    final code = codes[index];
                    final isUsed = code['isUsed'] as bool;
                    final isExpired = code['isExpired'] as bool;
                    final expiration = code['expiration'] as DateTime?;
                    
                    Color statusColor;
                    String status;
                    if (isUsed) {
                      statusColor = Colors.red;
                      status = 'Utilisé';
                    } else if (isExpired) {
                      statusColor = Colors.orange;
                      status = 'Expiré';
                    } else {
                      statusColor = Colors.green;
                      status = 'Valide';
                    }
                    
                    return ListTile(
                      leading: CircleAvatar(
                        backgroundColor: statusColor.withValues(alpha: 0.2),
                        child: Icon(
                          isUsed ? Icons.check : (isExpired ? Icons.timer_off : Icons.key),
                          color: statusColor,
                          size: 20,
                        ),
                      ),
                      title: Text(
                        code['code'],
                        style: TextStyle(
                          fontFamily: 'monospace',
                          fontWeight: FontWeight.bold,
                          decoration: isUsed ? TextDecoration.lineThrough : null,
                        ),
                      ),
                      subtitle: Text(
                        expiration != null
                            ? 'Expire: ${DateFormat('dd/MM/yyyy').format(expiration)}'
                            : 'Pas de date',
                      ),
                      trailing: Container(
                        padding: const EdgeInsets.symmetric(horizontal: 8, vertical: 4),
                        decoration: BoxDecoration(
                          color: statusColor.withValues(alpha: 0.2),
                          borderRadius: BorderRadius.circular(12),
                        ),
                        child: Text(
                          status,
                          style: TextStyle(color: statusColor, fontSize: 12),
                        ),
